	}
	int mint = 0;
	int maxt = 13;
	while (mint + 1 < maxt) {
		int mid = (mint + maxt) >> 1;
		if (ok(mid)) {
			mint = mid;
		}
		else {
			maxt = mid;
		}
	}
	if (ok(maxt)) {
		cout << maxt << endl;
	}
	else {
		cout << mint << endl;
	}

	return 0;
}

