      } else if ((i >> j) & 1) {
        se.pb(24 - s[j]);

      } else {
        se.pb(s[j]);
      }
    }
    sort(se.begin(), se.end());
    REP(i, se.size() - 1) { maxa = min(maxa, se[i + 1] - se[i]); }
    if (maxa == INF || maxa == 24) {
      maxa = 0;
    }
    maxe = max(maxa, maxe);
  }

  cout << maxe << endl;
  return 0;
}

