const double EPS = 1e-10;

int bitcount(int bits){
    int num;

    num = (bits >> 1) & 03333333333;
    num = bits - num - ((num >> 1) & 03333333333);
    num = ((num + (num >> 3)) & 0707070707) % 077;

    return num;
}

int n,m;
ll k;
string s;

int ans = 0;

int a[50];

struct UF{
	int data[51 * 2];
	int rk[51 * 2];
	int n;
	void init(int n){
		this->n = n;
		REP(i, n) {
			data[i] = i;
			rk[i] = 0;
		}
	}
	bool find(int i){
		return i==data[i] ? i : data[i] = find(data[i]);
	}
	bool same(int a, int b){
		return find(a) == find(b);
	}
	void unite(int a, int b){
		a = find(a);
		b = find(b);
		if(a==b)return;
		if(rk[a] < rk[b]){
			data[a] = b;
		}else{
			data[b] = a;
			if(rk[a] == rk[b]) rk[b]++;
		}
	}
};

UF tree;

bool solve(int x){
	tree.init(n);
	REP(i, 1<<n)if(bitcount(i)==2){
		int f=-1,s=0;
		REP(j, n) if(i & (1<<j)) {
			if(f<0) f = j;
			else s = j;
		};
		if(abs(a[f] - a[s]) < x){
			if(tree.same(f, s+n)) return false;
			tree.unite(f, s);
			tree.unite(f+n, s+n);
		}
		if(abs(a[f] + a[s] - 24) < x){
			if(tree.same(f, s)) return false;
			tree.unite(f, s+n);
			tree.unite(f+n, s);
		}
	}
	return true;
}

