  if(cnt[0]>1) return 0;
  if(cnt[12]>1) return 0;
  int res=0;
  REP(mask,1<<11) {
    VI X;
    if(cnt[0]) X.push_back(0);
    if(cnt[12]) X.push_back(12);
    REP(i,11) if(cnt[i+1]>0) {
      int t=i+1;
      if(cnt[t]>1) X.push_back(t),X.push_back(24-t);
      else X.push_back(((mask>>i)&1)?t:24-t);
    }
    sort(X.begin(),X.end());
    X.push_back(24);
    int ming=25;
    REP(i,SZ(X)-1) SMIN(ming,X[i+1]-X[i]);
//    dump(ming);
//    dumpAR(X);
    SMAX(res,ming);
  }
  return res;
}

