	void unite(int a, int b){
		a = find(a);
		b = find(b);
		if(a==b)return;
		if(rk[a] < rk[b]){
			data[a] = b;
		}else{
			data[b] = a;
			if(rk[a] == rk[b]) rk[b]++;
		}
	}
};

UF tree;

bool solve(int x){
	tree.init(n*2);
	REP(f, n) FOR(s, f+1, n){
		if(abs(a[f] - a[s]) < x){
			tree.unite(f, s+n);
			tree.unite(f+n, s);
			if(tree.same(f, f+n)) return false;
			if(tree.same(s, s+n)) return false;
		}
		if(abs(a[f] + a[s] - 24) < x){
			tree.unite(f, s);
			tree.unite(f+n, s+n);
			if(tree.same(f, f+n)) return false;
			if(tree.same(s, s+n)) return false;
		}
	}
	return true;
}

