int norm(int m, int n){
    return min(abs(n-m),24-abs(n-m));
}
int main(void) {

    int n;
    cin >> n;
    vector<int> d;
    for(int i=0;i<n;i++){
        int x;
        cin >> x;
        d.push_back(x);
    }
    int iru[24] ={0};
    iru[0] = 1;
    for(int i=0;i<n;i++){
        int a = d[i];
        int b = 24-d[i];
        int amin = 24; int bmin = 24;
        for(int j=0;j<24;j++){
            if(iru[j]){
                amin = min(amin,norm(a,j));
            }
        }
        for(int j=0;j<24;j++){
            if(iru[j]){
                amin = min(bmin,norm(b,j));
            }
        }
        if(amin<bmin){
            iru[a]=1;
        }else{
            iru[b]=1;
        }
    }
    int M = 0;
    int Max = 0;
    for(int i=1;i<24;i++){
        if(!iru[i]){
            M++;
        }else{
            M=0;
        }
        Max = max(Max,M);
    }
    cout << Max+1 << endl;
    return 0;
}

