    // }
  }
  int result = 0;
  for (int bit = 0; bit < (1 << 24); bit++){
    if (dp[N % 2][bit] == 0)continue;
    int res = INF;
    for (int i = 0; i <= 24; i++){
      for (int j = i + 1; j <= 24; j++){
        if (((bit >> i)&1) and ((bit >> j) &1)){
          res = min(res, min(j - i, 24 - (j - i)));
        }
      }
    }
    if (res == INF)continue;
    result = max(result, res);
  }
