    iru[0] = 1;
    for(int i=0;i<n;i++){
        int a = d[i];
        int b = 24-d[i];
        int amin = 24; int bmin = 24;
        for(int j=0;j<24;j++){
            if(iru[j]){
                amin = min(amin,norm(a,j));
            }
        }
        for(int j=0;j<24;j++){
            if(iru[j]){
                bmin = min(bmin,norm(b,j));
            }
        }
