        cout << 0 << endl;
        return 0;
    }

    bool asone[13];
    REP(i,13) asone[i] = false;
    REP(i,one.size()) asone[one[i]] = true;

    int ans = 0;
    REP(i,(1 << one.size())) {
        int trans[13];
        REP(j,one.size()) {
            if ((i >> j) & 1) {
                trans[one[j]] = one[j];
            } else {
                trans[one[j]] = 24 - one[j];
            }
