        REP(j,Dt.size()) if (asone[Dt[j]]) {
            Dt[j] = trans[Dt[j]];
        }
        int diff = 1e9;
        REP(j,Dt.size()) REP(k,Dt.size()) {
            if (j != k) {
                diff = min(diff, min(abs(Dt[j] - Dt[k]), abs(Dt[j] - (24 - Dt[k]))));
            }
        }
        ans = max(ans, diff);
    } 

    REP(i,ori_D.size()) ans = min(ans, ori_D[i]);

    cout << ans << endl;

    return 0;
}

