		res = std::min(res, copy[0] + 24 - copy[copy.size()-1]);
		ans = std::max(res, ans);
		return;
	}
	if (T[t[n]] == 2) {
		dfs(n + 2);
		return;
	}
	if (t[n] == 12) {
		dfs(n + 1);
		return;
	}
	dfs(n + 1);
	t[n] = 24 - t[n];
	dfs(n + 1);
	t[n] = 24 - t[n];
	return;
}
int main() {
	T[0]++;
	std::cin >> N;
	if (N == 1) {
		int tmp;
		std::cin >> tmp;
		std::cout << tmp << std::endl;
		return 0;
	}
	for (int i = 1; i <= N; i++) {
		int tmp;
		std::cin >> tmp;
		T[tmp]++;
	}
	if (T[0] > 1 || T[12] > 1) {
		std::cout << 0 << std::endl;
		return 0;
	}
	for (int i = 0; i <= 12; i++) {
		if (T[i] >= 1)t.emplace_back(i);
		if (T[i] >= 2)t.emplace_back(24 - i);
		if (T[i] >= 3) {
			std::cout << 0 << std::endl;
			return 0;
		}
	}
	dfs(1);
	std::cout << ans << std::endl;
	return 0;
}

