typedef long long ll;

struct Timer {

    double CYCLE_PER_SEC = 2400000000.0;
    unsigned long long BEGIN_CYCLE = 0;

    Timer() {}
    Timer(double cycle) : CYCLE_PER_SEC(cycle) {}

    unsigned long long int getCycle() {
        unsigned int low, high;
        __asm__ volatile ("rdtsc" : "=a" (low), "=d" (high));
        return ((unsigned long long int)low) | ((unsigned long long int)high << 32);
    }

    void start(void) {
        BEGIN_CYCLE = getCycle();
    }

    double get_time(void) {
        return (double)(getCycle() - BEGIN_CYCLE) / CYCLE_PER_SEC;
    }

};

struct XorShift {
    unsigned int x;
    XorShift () : x(2463534242U) {}
    unsigned int rand() {
        x ^= (x << 13);
        x ^= (x >> 17);
        x ^= (x << 5);
        return x;
    }
};

XorShift Random;

constexpr double TIME_LIMIT = 1.00;
constexpr double TEMP_START = 2.00;
constexpr double TEMP_END   = 0.30;
constexpr double TEMP_DIFF  = (TEMP_START - TEMP_END) / TIME_LIMIT;

bool prob(int diff, double cur_time) {
    if (diff >= 0) return true;
    double temp = TEMP_START - TEMP_DIFF * cur_time;
    double P = exp((double) diff / temp) * 4294967295.0;
    return Random.rand() < P;
}

int main() {

    int N; cin >> N;
    vector < int > D(N);
    REP(i,N) cin >> D[i];

    if (N == 1) {
        cout << D[0] << endl;
        return 0;
    }

    int atmost = 1e9;
    REP(i,D.size()) atmost = min(atmost, D[i]);

    Timer timer;
    timer.start();

    int BestScore = 0;
    int preScore = 0;
    while (true) {
        double cur_time = timer.get_time();
        int idx1 = Random.rand() % N;
        D[idx1] = 24 - D[idx1];
        int diff = 1e9;
        REP(i,D.size()) REP(j,D.size()) {
            if (i != j) {
                diff = min(diff, abs(D[i] - D[j]));
            }
        }
        diff = min(diff, atmost);
        int d = diff - preScore;
        if (prob(d, cur_time)) {
            preScore += d;
            if (preScore > BestScore) {
                BestScore = preScore;
            }
        } else {
            D[idx1] = 24 - D[idx1];
        }
        if (cur_time > TIME_LIMIT) break;
    }

    cout << BestScore << endl;

    return 0;
}

