void zipp() {
    map<int, int> zip;
    repeat(i, n) {
        zip[dd[i]]++;
    }

    int m = 0;
    for (auto p : zip) {
        repeat(i, min(2, p.second))
            dd[m++] = p.first;
    }

    n = m;
