    ww[n] = 0;
    for (int bit = 0; bit < 1 << n; ++bit) {
        int b = bit;
        repeat(i, n) {
            ww[i] = (b & 1) ? dd[i] : 24 - dd[i];
            b >>= 1;
        }
        int nice = 25;
        repeat(i, n) {
            iterate(j, i + 1, n + 1) {
                minset(nice, difftable[ww[i]][ww[j]]);
            }
        }

        maxset(best, nice);
    }
    cout << best << endl;
