map<pair<int, int>, int> memo;

int dfs(int i, int state) {
    if(i == n) {
        int mini = 24;
        for(int i : rep(0, 24)) {
            for(int j : rep(i + 1, 24)) {
                if(((state >> i) & 1) && ((state >> j) & 1)) {
                    if(abs(i - j) <= 12) {
                        mini = min(mini, abs(i - j));
                    } else {
                        mini = min(mini, 24 - abs(i - j));
                    }
                }
            }
        }

        return mini;
    }

    if(memo.count({i, state})) {
        return memo[{i, state}];
    }

    int ret = 0;

    {
        int x = d[i];

        if(!((state >> x) & 1)) {
            ret = max(ret, dfs(i + 1, state | (1 << x)));
        }
    }

    {
        int x = 24 - d[i];
        if(!((state >> x) & 1)) {
            ret = max(ret, dfs(i + 1, state | (1 << x)));
        }
    }

    return memo[{i, state}] = ret;
}

