void solve(vector<bool>& t, vector<int>& d, vector<int>& ans, int i){
    if(i > 12){
        vector<int> s;
        for(int j = 0; j < 24; j++){
            if(t[j]){
                s.push_back(j);
            }
        }
        int min = INF;
        for(int j = 0; j < (int)s.size()-1; j++){
            if(s[j+1] - s[j] < min){
                min = s[j+1] - s[j];
            }
        }
        ans.push_back(min);
        return ;
    }

    if(d[i] == 0){
        solve(t, d, ans, i+1);
    }else{
        t[i] = true;
        solve(t, d, ans, i+1);
        t[i] = false;
        t[24-i] = true;
        solve(t, d, ans, i+1);
        t[24-i] = false;
    }
}

