	int mint = INF;
	REP(i, n) {
		mint = min(min(d[i], 24 - d[i]), mint);
	}
	RREP(t, mint + 1) {
		Scc scc(2 * n);
		int f = 1;
		REP(i, n) {
			FOR(j, i + 1, n) {
				if (abs(d[i] - d[j]) < t) {
					if (min(abs(d[i] + d[j] - 24), d[i] + d[j]) < t) {
						goto next;
					}
					else {
						scc.add_edge(i, n + j);
						scc.add_edge(j, n + i);
					}
				}
				if (min(abs(d[i] + d[j] - 24),d[i]+d[j]) < t) {
					scc.add_edge(i, j);
					scc.add_edge(j, i);
				}
			}
		}
		scc.calc();

		REP(i, n) {
			if (scc.cmp[i] == scc.cmp[n + i])f = 0;
		}
		if (f) {
			cout << t << endl;
