                int fi = f - fst;
                int se = bst - f;
                fi = min(fi, 24 - fi);
                se = min(se, 24 - se);
                if(f != fst) val = min(val, fi);
                if(f != bst) val = min(val, se);
            }
        }
        if(val == 25) val = 0;
        ans = max(ans, val);
        //cerr << val << endl;
    }
    cout << ans << endl;
    return 0;
