int calcMin(vector<int> &d)
{
    int n = d.size();
    int m = INT32_MAX;
    for(int i=0; i<n; i++){
        for(int j=0; j<n; j++){
            if(i==j){ continue; }
            m = min(m, dmin(abs(d[i] - d[j])));
        }
    }
    return m;
}

int calcMax(vector<int> &d)
{
    int n = (int)pow(2, d.size());
    int valMax = INT32_MIN;
    for(uint32 i=0; i<n; i++){
        vector<int> dd = d;
        for(int j=0; j<12; j++){
            if(((i>>j)&1) == 1){
                dd[j] = 24 - dd[j];
            }
        }
        int val = calcMin(dd);
        valMax = max(valMax, val);
    }
    return valMax;
}
