#include <bits/stdc++.h>
using namespace std;
static const int64_t INFL = 0x3F3F3F3F3F3F3F3FLL;
typedef int64_t ll;
typedef vector<ll> vll;
typedef vector<vll> vvll;
struct Solution {
    void solve(std::istream& in, std::ostream& out) {
        ll n;
        in >> n;
        n++;
        vll d(n);
        vvll dist(n, vll(n));
        for (int i = 1; i < n; i++) {
            in >> d[i];
        }
        ll ans = INFL;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (i == j) { continue; }
                dist[i][j] = max(max(abs(d[i] - d[j]) % 24, abs((24 - d[i]) - (24 - d[j])) % 24), max(abs((24 - d[i]) - d[j]) % 24, abs(d[i] - (24 - d[j])) % 24));
                ans = min(ans, dist[i][j]);
            }
        }

        out << ans << '\n';
    }
};
void solve(std::istream& in, std::ostream& out) {
    out << std::setprecision(12);
    Solution solution;
    solution.solve(in, out);
}
