        }
        res = min(res, a);
        return res;
    }
    if(cnt[d] == 0) return dfs(d+1);
    if(cnt[d] == 1){
        c[d] = 1;
        res = max(res, dfs(d+1));
        c[d] = 0;
        c[24-d] = 1;
        res = max(res, dfs(d+1));
        c[24-d] = 0;
    }
    if(cnt[d] == 2){
        c[d] = 1;
        c[24-d] = 1;
        return dfs(d+1);
        c[d] = 0;
        c[24-d] = 0;
    }
    if(cnt[d] > 2) return 0;
    return res;
}

