        num.push_back(j*(((i>>(j-1))&1)?1:-1));
      }
    }

    std::sort(all(num));

    ll si=num.size();

    ll val=std::min(num[si-1]-num[0],24ll+num[0]-num[si-1]);
    rep(j,1,si){
      chmin(val,std::min(num[j]-num[j-1],24+num[j-1]-num[j]));
    }

    chmax(ans,val);
  }

  std::cout<<ans<<"\n";

  return 0;
}

