void solve(int t, int bit) {
	if (t == 13) {
		int mi = 1 << 30;
		for (int i = 0; i < 24; i++) {
			if ((bit&(1 << i)) == 0) continue;
			for (int j = i + 1; j < 24; j++) {
				if ((bit&(1 << j)) == 0) continue;
				int d = abs(i - j);
				mi = min(mi, min(d, 24 - d));
