int get(ll n)
{
    ll min = 12;
    REP(i, 24)
    {
        FOR(j, 24, i + 1)
        {
            if (((n >> i) & 1) == 0 || ((n >> j) & 1) == 0)
                continue;
            min = std::min(min, std::min(j - i, 24 + i - j));
        }
    }
    return min;
}

int solve(ll n = 0, int index = 0)
{
    if (index > 12)
        return get(n);

    if (T[index] == 0)
        return solve(n, index + 1);

    ll n0 = 1 << index;
    ll n1 = 1 << ((24 - index) % 24);
    if (T[index] == 2)
        return solve(n | n0 | n1, index + 1);
    return std::max(solve(n | n0, index + 1), solve(n | n1, index + 1));
}

