	for (int i = 1; i < 12; i++) {
		if (G[i] > 2) {
			printf("0\n");
			return 0;
		}
	}
	if (G[12] > 1) { printf("0\n"); return 0;}

	cn = 0;
	for (int i = 1; i < 12; i++) {
		if (G[i] == 1) {
			P[cn] = i;
			cn++;
		}
	}

	ans = 0;
	for (int i = 0; i < pow2(cn); i++) {
		k = i;
		for (int j = 0; j < cn; j++) {
			M[j] = k % 2;/*M[j]が0なら午前*/
			k = k / 2;
		}

		for (int j = 0; j < 24; j++) {
			L[j] = 0;
		}

