			else { L[24 - P[j]] = 1; }
		}


		s = 24;
		last = 0;
		for (int j = 1; j < 24; j++) {
			if (L[j] == 1) { 
				if (s > j - last) { s = j - last; } 
				last = j;
			}
		}
		if (s > 24 - last) { s = 24 - last; }
		if (ans < s) { ans = s; }

	}

	printf("%d\n", ans);

}

