int dfs(int dfs_i, const std::vector<int>& ds, std::vector<int>& xs) {
    if (dfs_i == ds.size()) {
        int res = 24,
            l = 0;
        for (int i = 1; i < 24; i++) {
            if (xs[i]) {
                res = std::min({res, 24 - i, i - l});
                l = i;
            }
        }
        return res;
    }

    int d = ds[dfs_i],
        res = 0;

    for (int i = 0; i < 2; i++) {
        if (xs[d] == 0) {
            xs[d]++;
            res = std::max(res, dfs(dfs_i + 1, ds, xs));
            xs[d]--;
        }
        d = (24 - d) % 24;
    }
    return res;
}

