const int MAXN = 50;
const int MAXD = 12;

int n;
int d[MAXN];

int cnt[MAXD + 1];
int opt[MAXD], nopt;
int cur[MAXN], ncur;
int solve() {
	REPE(i, MAXD) cnt[i] = 0; REP(i, n) ++cnt[d[i]];
	REPE(i, MAXD) if (cnt[i] >= (i == 0 ? 1 : i == MAXD ? 2 : 3)) return 0;
	nopt = 0; FORE(i, 1, MAXD - 1) if (cnt[i] == 1) opt[nopt++] = i;
	int ret = 0;
	REP(mask, 1 << nopt) {
		ncur = 0;
		cur[ncur++] = 0;
		FORE(i, 1, MAXD - 1) if (cnt[i] == 2) cur[ncur++] = i, cur[ncur++] = 2 * MAXD - i;
		REP(i, nopt)  cur[ncur++] = (mask&(1 << i)) == 0 ? opt[i] : 2 * MAXD - opt[i];
		if (cnt[MAXD] == 1) cur[ncur++] = MAXD;
		assert(ncur == n + 1); sort(cur, cur + ncur);
		int curval = INT_MAX; REP(i, ncur) curval = min(curval, (cur[(i + 1) % ncur] - cur[i] + 2 * MAXD) % (2 * MAXD));
		//printf("%x:", mask); REP(i, n) printf(" %d", cur[i]); printf(" -> %d\n", curval);
		if (curval > ret) ret = curval;
	}
	return ret;
}

