int mindiff(vector<int> &a){
    sort(a.begin(), a.end());
    int ans = 100;
    int N = a.size();
    for(int i=0; i<N; ++i){
        int timeDif = min(abs(a[(i+1)%N]-a[i]), 24-abs(a[(i+1)%N]-a[i]));
        chmin(ans, timeDif);
    }
    return ans;
}

