int rec(int i, int bit)
{
	if (i == 13) {
		int ret = iinf;
		int d = iinf;
		for (int j = 0; j < 48; j++) {
			if (bit & (1 << (j % 24))) {
				ret = min(ret, d);
				d = 0;
			}
			d++;
		}
		return ret;
	}

