		case 0:
			return rec(i + 1, bit);
		case 1:
			return max(rec(i + 1, bit | (1 << i)), rec(i + 1, bit | (1 << (24 - i))));
		case 2:
			return rec(i + 1, bit | (1 << i) | (1 << (24 - i)));
	}
	return 0;
}

