void solve(vector<bool>& t, vector<int>& d, vector<int>& ans, int i){
    if(i > 12){
        vector<int> s;
        for(int j = 0; j < 24; j++){
            if(t[j]){
                s.push_back(j);
            }
        }
        int minT = INF;
        for(int j = 0; j < (int)s.size()-1; j++){
            for(int k = j+1; k < (int)s.size(); k++){
                int time = s[k] - s[j];
                time = min(time, 24 - time);
                if(time < minT){
                    minT = time;
                }
            }
        }
        ans.push_back(minT);
        return ;
    }

    if(d[i] == 0){
        solve(t, d, ans, i+1);
    }else{
        t[i] = true;
        solve(t, d, ans, i+1);
        t[i] = false;
        t[24-i] = true;
        solve(t, d, ans, i+1);
        t[24-i] = false;
    }
}

