    return ans;
  }
  int res = 0;
  if (t[d[index]] == 1) {
  }
  else
  {
    t[d[index]] = 1;
    chmax(res, rec(n, index+1));
    t[d[index]] = 0;
  }
  int hoge = 24 - d[index];
