    printf("0\n");return 0;
  }
  ROP(i,N){
    scanf(" %d",&D[i]);
    DD[i]=D[i];
  }

  ROP(i,N){
    int MIN=12,cnt=0;DD[i]=24-D[i];
    ROP(j,N){
      if(i==j) continue;
      MIN=min(MIN,abs(DD[i]-D[j]));
    }
    DD[i]=D[i];
