		if (cnt[d])
		{
			cnt[24-d]++;
		}
		cnt[d]++;
	}
	int ans = 0;
	for (int bit = 0; bit < 1<<24; bit++)
	{
		vector<int> v;
		v.push_back(0);
		for (int i = 0; i < 24; i++)
		{
			if (!cnt[i+1]) continue;
			v.push_back((bit>>i&1)?i+1:23-i);
		}
		sort(v.begin(), v.end());
		int n = v.size(), res = min(v[0]-v[n-1]+24, v[n-1]-v[0]);
		for (int i = 0; i < n-1; i++)
		{
			int diff = v[i+1]-v[i];
			res = min(res, min(24-diff, diff));
		}
		ans = max(ans, res);
	}
	cout << ans << endl;
}

