ll dp[100010][6];
int pi[100010][6];
ll dist[7][7];

ll calc(ll now, ll b, int c, int p, int v) {

	if (b < 0) return INF;
	ll dis = now - b - 1LL;

	ll val = dp[b][p] + max(dis, dist[p][c]);
	if (v > val) {
		val = min(val, calc(now, pi[b][p], c, p, val));
	}
	return val;
}

