  bool z[50][2], zz[50][2];
  FOR(i, 0, n-1)FOR(j, i+1, n){
    if(_tm(d[i]-d[j]) < a){
      G[i][0].pb(mp(j, 1));
      G[i][1].pb(mp(j, 0));
      G[j][0].pb(mp(i, 1));
      G[j][1].pb(mp(i, 0));
    }
    if(_tm(d[i]+d[j]) < a){
      G[i][0].pb(mp(j, 0));
      G[i][1].pb(mp(j, 1));
      G[j][0].pb(mp(i, 0));
      G[j][1].pb(mp(i, 1));
    }
  }
  queue<pii> q;
  FOR(i, 0, n){
    if(z[i][0]) continue;
    q.push(mp(i, 0));
    z[i][0] = true;
    Fill(zz, false);
    zz[i][0] = true;
    while(sz(q)){
      pii t = q.front();
      q.pop();
      each(itr, G[t.fi][t.se]){
        if(zz[itr->fi][1-itr->se]) return false;
        if(zz[itr->fi][itr->se]) continue;
        zz[itr->fi][itr->se] = true;
        z[itr->fi][itr->se] = true;
        q.push(*itr);
      }
    }
  }
  return true;
}
main(){
