  FOR(i, 0, n){
    cin >> d[i];
    mins(minv, d[i]);
  }
  int l = 0, r = minv + 1, mid;
  while(r - l > 1){
    mid = (l+r)/2;
    if(check(mid)){
      l = mid;
    }else{
      r = mid;
    }
  }
  cout << l << endl;
  return 0;
}

