// ソート関数 - int昇順
int
fSortFncIu(
	const void *pzpVal1			// <I> 値１
	, const void *pzpVal2		// <I> 値２
)
{
	int *lipVal1 = (int *)pzpVal1;
	int *lipVal2 = (int *)pzpVal2;

	// int昇順
	if (*lipVal1 > *lipVal2) {
		return 1;
	}
	else if (*lipVal1 < *lipVal2) {
		return -1;
	}

	return 0;
}

// 時刻 - 検索
int
fSrhTime(
	int piSNo					// <I> 開始位置
)
{
	int i;

	for (i = piSNo; ; i++) {
		if (i >= D_HOUR_MAX) {
			i = 0;
		}
		if (sc1Time[i] == D_ON) {
			return i;
		}
	}

	return 0;
}

// 時刻 - セット
int
fSetTime(
	int piDNo					// <I> 時差
)
{
	// 終了
	if (piDNo >= siDCnt) {

		// 開始位置
		int liStart = fSrhTime(0);
		int liNow = liStart;

		// 最小時間 - 取得
		int liMin = INT_MAX;
		while (1) {

			// 次の位置
			int liNext = fSrhTime(liNow + 1);

			// 時間
			int liTime = abs(liNext - liNow);
			if (liMin > liTime) {
				liMin = liTime;
			}

			// 次回用
			if (liNext == liStart) {
				break;
			}
			liNow = liNext;
		}

		// 最大時間 - 更新
		if (siMax < liMin) {
			siMax = liMin;
		}

		return 0;
	}

	// 時刻１
	int liDF = si1DF[piDNo];
	if (sc1Time[liDF] == D_OFF) {
		sc1Time[liDF] = D_ON;
		fSetTime(piDNo + 1);
		sc1Time[liDF] = D_OFF;
	}

	// 時刻２
	liDF = (24 - si1DF[piDNo]) % 24;
	if (sc1Time[liDF] == D_OFF) {
		sc1Time[liDF] = D_ON;
		fSetTime(piDNo + 1);
		sc1Time[liDF] = D_OFF;
	}

	return 0;
}

