// ソート関数 - int昇順
int
fSortFncIu(
	const void *pzpVal1			// <I> 値１
	, const void *pzpVal2		// <I> 値２
)
{
	int *lipVal1 = (int *)pzpVal1;
	int *lipVal2 = (int *)pzpVal2;

	// int昇順
	if (*lipVal1 > *lipVal2) {
		return 1;
	}
	else if (*lipVal1 < *lipVal2) {
		return -1;
	}

	return 0;
}

// 時刻 - 検索
int
fSrhTime(
	int piSNo					// <I> 開始位置
)
{
	int i;

	for (i = piSNo; ; i++) {
		if (i >= D_HOUR_MAX) {
			i = 0;
		}
		if (sc1Time[i] == D_ON) {
			return i;
		}
	}

	return 0;
}

// 時刻 - セット
int
fSetTime(
	int piDNo					// <I> 時差
)
{
	// 終了
	if (piDNo >= siDCnt) {

		// 開始位置
		int liStart = fSrhTime(0);
		int liNow = liStart;

		// 最小時間 - 取得
		int liMin = INT_MAX;
		while (1) {

			// 次の位置
			int liNext = fSrhTime(liNow + 1);

			// 時間
			int liTime = (liNext - liNow + D_HOUR_MAX) % D_HOUR_MAX;
			if (liMin > liTime) {
				liMin = liTime;
			}

			// 次回用
			if (liNext == liStart) {
				break;
			}
			liNow = liNext;
		}

		// 最大時間 - 更新
		if (siMax < liMin) {
			siMax = liMin;
		}

		return 0;
	}

	// 時刻１
	int liDF = si1DF[piDNo];
	if (sc1Time[liDF] == D_OFF) {
		sc1Time[liDF] = D_ON;
		fSetTime(piDNo + 1);
		sc1Time[liDF] = D_OFF;
	}

	// 時刻２
	liDF = (D_HOUR_MAX - si1DF[piDNo]) % D_HOUR_MAX;
	if (sc1Time[liDF] == D_OFF) {
		sc1Time[liDF] = D_ON;
		fSetTime(piDNo + 1);
		sc1Time[liDF] = D_OFF;
	}

	return 0;
}

// 実行メイン
int
fMain(
)
{
	int i;
	char lc1Buf[1024];

	// データ - 初期化
	memset(sc1Time, D_OFF, sizeof(sc1Time));
	siMax = 0;

	// 時差数 - 取得
	fgets(lc1Buf, sizeof(lc1Buf), szpFpI);
	sscanf(lc1Buf, "%d", &siDCnt);

	// 時差 - 取得
	si1DF[0] = 0;
	for (i = 1; i <= siDCnt; i++) {
		fscanf(szpFpI, "%d", &si1DF[i]);
	}
	siDCnt++;
	fgets(lc1Buf, sizeof(lc1Buf), szpFpI);
	qsort(si1DF, siDCnt, sizeof(int), fSortFncIu);

	// 時刻 - セット
	fSetTime(0);

	return 0;
}

