import java.util.Scanner;

public class Main {

	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		int[] peopleCount = new int [13];
		for (int i=0;i<n;i++) {
			peopleCount[sc.nextInt()]++;
		}

		System.out.println(solve(peopleCount));

	}

	static int solve(int[] peopleCount) {
		if (peopleCount[0]>0) {
			return 0;
		}
		for (int i=1;i<=11;i++) {
			if (peopleCount[i]>2) {
				return 0;
			}
		}
		if (peopleCount[12]>1) {
			return 0;
		}

		int ans = 0;

		for (int i=0;i<2048;i++) {
			boolean[] used = new boolean[24];
			used[12]=true;
			for (int j=1;j<=11;j++) {
				if (peopleCount[j]==2) {
					used[12+j]=true;
					used[12-j]=true;
				} else if (peopleCount[j]==1) {
					if ((i & (1<<(j-1))) > 0) {
						used[12+j]=true;
					} else {
						used[12-j]=true;
					}
				}
			}
			if(peopleCount[12]==1) {
				used[0]=true;
			}
			int s = 24;
			int pos = -1;
			for(int j=0;j<48;j++) {
				if(used[j%24]) {
					if(pos >= 0) {
						s=Math.min(j-pos, s);
					}
					pos = j;
				}
			}

			ans = Math.max(s, ans);

		}
		return ans;
	}

}

import java.io.*;
import java.util.*;
import java.math.*;
import java.awt.Point;

public class Main {
    InputStream is;
    PrintWriter out;
    String INPUT = "";
    int n;
    void solve()
    {
        n = ni();
        int[] d  = new int[n+1];
        d[0] = 0;
        for(int i = 0; i < n; i++){
            d[i+1] = ni();
        }
        n++;
        boolean[] seen = new boolean[25];
        int left = 0;
        int right = 24;
        while(left+1<right){
            int res = (left+right)/2;
            if(f(d,res,0,seen)) left = res;
            else right = res;
        }
        out.println(left);

    }

    boolean f(int[] d, int sub, int k, boolean[] seen){
        if(k==n){
            return true;
        }
        boolean res = false;
        for(int i = 0; i < 2; i++){
            if(i==1) d[k] = 24-d[k];
            int[] dc  = new int[n];
            boolean[] seenc  = new boolean[25];
            for(int j = 0; j < n; j++) dc[j] = d[j];
            seenc[d[k]] = true;
            if(d[k]==0) seenc[24] = true;
            if(d[k]==24) seenc[0] = true;
            for(int j = 0; j <= 24; j++){
                if(d[k]-sub < j && j < d[k]+sub) seenc[j] = true;
                if(d[k]-sub < 0 && j > d[k]-sub+24) seenc[j] = true;
                if(d[k]-sub > 24 && j < d[k]-sub-24) seenc[j] = true;
            }
            if(!seen[d[k]]){
                res |= f(dc, sub, k+1, seenc);
            }
        }   
        return res;
    }   

    void run() throws Exception
    {
        is = INPUT.isEmpty() ? System.in : new ByteArrayInputStream(INPUT.getBytes());
        out = new PrintWriter(System.out);

        long s = System.currentTimeMillis();
        solve();
        out.flush();
        if(!INPUT.isEmpty())tr(System.currentTimeMillis()-s+"ms");
    }

    public static void main(String[] args) throws Exception { new Main().run(); }

    private byte[] inbuf = new byte[1024];
    private int lenbuf = 0, ptrbuf = 0;

    private int readByte()
    {
        if(lenbuf == -1)throw new InputMismatchException();
        if(ptrbuf >= lenbuf){
            ptrbuf = 0;
            try { lenbuf = is.read(inbuf); } catch (IOException e) { throw new InputMismatchException(); }
            if(lenbuf <= 0)return -1;
        }
        return inbuf[ptrbuf++];
    }

    private boolean isSpaceChar(int c) { return !(c >= 33 && c <= 126); }
    private int skip() { int b; while((b = readByte()) != -1 && isSpaceChar(b)); return b; }

    private double nd() { return Double.parseDouble(ns()); }
    private char nc() { return (char)skip(); }

    private String ns()
    {
        int b = skip();
        StringBuilder sb = new StringBuilder();
        while(!(isSpaceChar(b) && b != ' ')){
            sb.appendCodePoint(b);
            b = readByte();
        }
        return sb.toString();
    }

    private char[] ns(int n)
    {
        char[] buf = new char[n];
        int b = skip(), p = 0;
        while(p < n && !(isSpaceChar(b))){
            buf[p++] = (char)b;
            b = readByte();
        }
        return n == p ? buf : Arrays.copyOf(buf, p);
    }

    private char[][] nm(int n, int m)
    {
        char[][] map = new char[n][];
        for(int i = 0;i < n;i++)map[i] = ns(m);
        return map;
    }

    private int[] na(int n)
    {
        int[] a = new int[n];
        for(int i = 0;i < n;i++)a[i] = ni();
        return a;
    }

    private int ni()
    {
        int num = 0, b;
        boolean minus = false;
        while((b = readByte()) != -1 && !((b >= '0' && b <= '9') || b == '-'));
        if(b == '-'){
            minus = true;
            b = readByte();
        }

        while(true){
            if(b >= '0' && b <= '9'){
                num = num * 10 + (b - '0');
            }else{
                return minus ? -num : num;
            }
            b = readByte();
        }
    }

    private long nl()
    {
        long num = 0;
        int b;
        boolean minus = false;
        while((b = readByte()) != -1 && !((b >= '0' && b <= '9') || b == '-'));
        if(b == '-'){
            minus = true;
            b = readByte();
        }

        while(true){
            if(b >= '0' && b <= '9'){
                num = num * 10 + (b - '0');
            }else{
                return minus ? -num : num;
            }
            b = readByte();
        }
    }

    private static void tr(Object... o) { System.out.println(Arrays.deepToString(o)); }

    static class ArrayUtils {
        public static void fill(double[][] array, double value) {
            for (double[] a : array) Arrays.fill(a, value);
        }

        public static void fill(double[][][] array, double value) {
            for (double[][] l : array) fill(l, value);
        } 
    }
}

