public class Main {
  private static void solve() {
    int n = ni();
    int[] d = na(n);
    int[] count = new int[13];
    count[0]++;
    for (int x : d) {
      count[x]++;
      if (count[x] == 3) {
        System.out.println(0);
        return;
      }
    }

    List<Integer> list1 = new ArrayList<>();
    List<Integer> list2 = new ArrayList<>();
    for (int i = 0; i <= 12; i++) {
      if (count[i] == 1) {
        list1.add(i);
      } else if (count[i] == 2) {
        list2.add(i);
      }
    }

    int max = 0;
    int m = list1.size();
    for (int i = 0; i < (1 << m); i ++) {
      List<Integer> l = new ArrayList<>();
      for (int v : list2) {
        l.add(v);
        l.add(24 - v);
      }

      for (int j = 0; j < m; j ++) {
        int u = list1.get(j);
        if (((i >> j) & 1) == 0) {
          l.add(u);
        } else {
          l.add(24 - u);
        }
      }
      int s = l.size();
      int min = Integer.MAX_VALUE;
      for (int k = 0; k < s; k ++) {
        for (int p = k + 1; p < s; p ++) {
          int dd = Math.abs(l.get(k) - l.get(p));
          min = Math.min(Math.min(dd, 24 -dd), min);
        }
      }
      max = Math.max(min, max);
    }
    System.out.println(max);

  }



