public class Main  {
	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		int[] num = new int[n+1];
		num[0] = 0;
		for(int i = 0; i < n; i++) {
			num[i+1] = sc.nextInt();
		}
		Arrays.sort(num);
		int minWide_P = 0;
		int minWide = 24;
		int[] wide = new int[n];
		for(int i = 0; i < n; i++) {
			wide[i] = num[i+1] - num[i];
			if(wide[i] < minWide) {
				minWide = wide[i];
				minWide_P = i;
			}
		}

		if(n == 1) {
			System.out.println(wide[0]);
			return;
		}

		if(minWide_P == 0) {
			System.out.println(minWide);
		} else if(minWide_P == n - 1){
			System.out.println(Math.min(wide[minWide_P - 1] + wide[minWide_P], -num[n] + 24-num[minWide_P]));
		} else {
			int w1 = wide[minWide_P - 1];
			int w2 = wide[minWide_P + 1];

			int min;
			int max;
			if(w1 >= w2) {
				min = w2;
				max = w1;
				minWide_P++;
			} else {
				min = w1;
				max = w2;
				minWide_P = minWide_P;
			}

			if(minWide + min < max) {
				System.out.println(Math.min(minWide + min, 24 - num[minWide_P] - num[n]));
			} else {
				System.out.println(minWide);
			}
		}
	}
}

import java.util.*;

public class Main {
    public static void main(String [] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int [] d = new int[n + 1];
        PriorityQueue<Integer> time = new PriorityQueue<>();
        time.add(0);
        d[0] = 0;
        for (int i = 1; i <= n; ++i) {
            int current = scanner.nextInt();
            current = Math.min(current, 24 - current);
            time.add(current);
            d[i] = current;
        }
        int max = 0;
        while (time.size() == n + 1) {
            int current = time.poll();
            int originalCurrent = current;
            int currentMin = 24;
       //     System.out.println("current: " + current);
            PriorityQueue<Integer> copy = new PriorityQueue<>(time);
            while (!time.isEmpty()) {
                int nextTime = time.poll();
           //     System.out.println("next: " + nextTime);
                currentMin = Math.min(currentMin, nextTime - current);
                current = nextTime;
            }
       //     System.out.println("currentMin: " + currentMin);
            max = Math.max(max, currentMin);
    //        System.out.println("max: " + max);
            if (24 - originalCurrent > originalCurrent) {
                copy.add(24 - originalCurrent);
            //    System.out.println("push: " + (24 - originalCurrent));

            }
        //    System.out.println("==============");
            time = new PriorityQueue<>(copy);
        }
        if (max == 24) {
            System.out.println(0);
            return;
        }
        System.out.println(max);

    }
}

