class Diff implements Comparable<Diff>{
	int city1;
	int c1mode;
	int city2;
	int c2mode;
	int time;

	public Diff(int c1, int m1,  int c2, int m2, int time){
		city1 = c1;
		c1mode = m1;
		city2 = c2;
		c2mode = m2;
		this.time = time;
	}

	public int compareTo(Diff d){
		return d.time - this.time;
	}
}

