public class Main {

	public static void main(String[] args) throws IOException {
		// TODO 自動生成されたメソッド・スタブ
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		int n = Integer.parseInt(br.readLine());
		String[] tmpArray = br.readLine().split(" ");

		int time[] = new int[(n + 1)*2];

		for(int i = 1; i <= n; i++){
			int tmp = Integer.parseInt(tmpArray[i-1]);

			if(tmp != 0 && tmp != 24){
				time[i*2] = tmp;
				time[i*2 + 1] = 24 - tmp;
			}
			else {
				time[i*2] = 0;
				time[i*2 + 1] = 0;
			}
		}

		//debug
//		for(int i = 0; i < time.length ; i++){
//			System.out.print(time[i]+" ");
//		}
//		System.out.println();

		ArrayList<Diff> diffs = new ArrayList<Diff>();
		for(int i = 0 ; i < n; i++){
			for(int j = i + 1; j <= n; j++){
				for(int k = 0; k < 2; k++){
					for(int l = 0; l < 2; l++){
						diffs.add(new Diff(i, k, j, l, Math.abs(time[i*2 + k] - time[j*2 + l])));
					}
				}
			}
		}

		Collections.sort(diffs);
		//debug
//		for(int i = 0; i < diffs.size(); i++){
//			System.out.print(diffs.get(i).time+" ");
//		}
//		System.out.println();

		//現実的にそのような組み合わせが可能かをチェックする
		for(int i = 0; i < diffs.size(); i++){
			boolean available = true;
			Diff tmpDiff = diffs.get(i);

			int minTime = tmpDiff.time;

			for(int j = 0; j < n; j++){
				if(j == tmpDiff.city1){
					continue;
				}

				int tmpTime = minTime;

				if(j == tmpDiff.city2){
					tmpTime = Math.min(Math.abs(time[j*2 + tmpDiff.c2mode] - time[(j+1)*2 + 0]), tmpTime);
					tmpTime = Math.min(Math.abs(time[j*2 + tmpDiff.c2mode] - time[(j+1)*2 + 1]), tmpTime);
				}
				else if(j + 1 == tmpDiff.city1){
					tmpTime = Math.min(Math.abs(time[j*2 + 0] - time[(j+1)*2 + tmpDiff.c1mode]), tmpTime);
					tmpTime = Math.min(Math.abs(time[j*2 + 1] - time[(j+1)*2 + tmpDiff.c1mode]), tmpTime);
				}
				else {
					tmpTime = Math.min(Math.abs(time[j*2 + 0] - time[(j+1)*2 + 0]), tmpTime);
					tmpTime = Math.min(Math.abs(time[j*2 + 0] - time[(j+1)*2 + 1]), tmpTime);
					tmpTime = Math.min(Math.abs(time[j*2 + 1] - time[(j+1)*2 + 0]), tmpTime);
					tmpTime = Math.min(Math.abs(time[j*2 + 1] - time[(j+1)*2 + 1]), tmpTime);
				}

				if(tmpTime < minTime){
					available = false;
					break;
				}
			}

			if(available){
				System.out.println(minTime);
				return;
			}

		}
	}

}

class Diff implements Comparable<Diff>{
	int city1;
	int c1mode;
	int city2;
	int c2mode;
	int time;

	public Diff(int c1, int m1,  int c2, int m2, int time){
		city1 = c1;
		c1mode = m1;
		city2 = c2;
		c2mode = m2;
		this.time = time;
	}

	public int compareTo(Diff d){
		return d.time - this.time;
	}
}

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;
import java.util.Scanner;

public class Main {
    int n;
    int[] ds;
    int[] cnts = new int[13];
    boolean[] ts = new boolean[24];
    int ans = -1;
    public static void main(String[] args) {
        Main m = new Main();
        m.read();
        m.solve();
    }

    private void read() {
        Scanner sc = new Scanner(System.in);
        n = sc.nextInt();
        ds = new int[n];
        cnts[0]++;
        for (int i = 0 ; i < n; i++) {
            ds[i] = sc.nextInt();
            cnts[ds[i]]++;
        }
    }

    private void solve() {
        ts[0] = true;
        int mx = Arrays.stream(cnts).max().getAsInt();
        if (mx >= 3 || cnts[0] >= 2) {
            System.out.println(0);
            return;
        }
        ArrayList<Integer> ls = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            if (cnts[ds[i]] == 2) {
                ts[ds[i] % 24] = true;
                ts[(24 - ds[i]) % 24] = true;
            }
            else {
                ls.add(ds[i]);
            }
        }
        int[] as = new int[ls.size()];
        for (int i = 0; i < as.length; i++)
            as[i] = ls.get(i);
        dfs(0, as);
        System.out.println(ans);

    }

    private void dfs(int i, int[] as) {
        if (i == as.length) {
            calc();
            return;
        }
        ts[as[i] % 24] = true;
        dfs(i+1, as);
        ts[as[i] % 24] = false;
        ts[(24 - as[i]) % 24] = true;
        dfs(i+1, as);
        ts[(24 - as[i]) % 24] = false;
    }

    private void calc() {
        int mn = 13;
        int old = 0;
        for (int i = 1; i < ts.length; i++) {
            if (!ts[i])
                continue;
            mn = Math.min(mn, i - old);
            old = i;
        }
        mn = Math.min(mn, 24 - old);
        ans = Math.max(ans, mn);
    }
}

import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int [] d = new int[n + 1];
        d[0] = 0;
        for (int i = 1; i <= n; ++i) {
            d[i] = scanner.nextInt();
        }
        Arrays.sort(d);
        for (int i = 0; i <= n; ++i) {
            if (i % 2 == 0) {
                d[i] = 24 - d[i];
            }
        }
        int min = 24;
        for (int i = 0; i < n; ++i) {
            for (int j = i + 1; j <= n; ++j) {
                int diff = Math.abs(d[i] - d[j]);
                int current = Math.min(24 - diff, diff);
                min = Math.min(min, current);
            }
        }

        System.out.println(min);
    }
}

import java.io.OutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.PrintWriter;
import java.util.StringTokenizer;
import java.io.IOException;
import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.io.InputStream;

/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 */
public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        InputReader in = new InputReader(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        TimeGap solver = new TimeGap();
        solver.solve(1, in, out);
        out.close();
    }

    static class TimeGap {
        public void solve(int testNumber, InputReader in, PrintWriter out) {
            int ans = 0;

            int N = in.nextInt();
            int[] D = new int[N];
            for (int i = 0; i < N; i++) D[i] = in.nextInt();

            int[][] mimx = new int[N][2];
            for (int i = 0; i < N; i++) mimx[i] = new int[]{Math.min(D[i], 24 - D[i]), Math.max(D[i], 24 - D[i])};


            for (int a = 0; a <= 1; a++) {
                for (int b = 0; b <= 1; b++) {
                    int mini = Integer.MAX_VALUE;
                    for (int i = 0; i < N; i++) {
                        for (int j = i + 1; j < N; j++) {
                            mini = Math.min(mini, Math.abs(mimx[i][a] - mimx[j][b]));
                        }
                    }
                    ans = Math.max(ans, mini);
                }
            }
            out.println(ans == Integer.MAX_VALUE ? 0 : ans);
        }

    }

    static class InputReader {
        public BufferedReader reader;
        public StringTokenizer tokenizer;

        public InputReader(InputStream stream) {
            reader = new BufferedReader(new InputStreamReader(stream), 32768);
            tokenizer = null;
        }

        public String next() {
            while (tokenizer == null || !tokenizer.hasMoreTokens()) {
                try {
                    tokenizer = new StringTokenizer(reader.readLine());
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
            }
            return tokenizer.nextToken();
        }

        public int nextInt() {
            return Integer.parseInt(next());
        }

    }
}

import java.io.*;
import java.util.*;
import java.math.*;
import java.awt.Point;

public class Main {
    InputStream is;
    PrintWriter out;
    String INPUT = "";
    int n;
    void solve()
    {
        n = ni();
        int[] d  = new int[n+1];
        d[0] = 0;
        for(int i = 0; i < n; i++){
            d[i+1] = ni();
        }
        n++;
        boolean[] seen = new boolean[25];
        int min = 0;
        int ans = 0;
        for(int i = 0; i <= 24; i++){
            int[][] e = new int[n][2];
            e[0][0] = i;
            e[0][1] = i;
            for(int j = 0; j < n-1; j++){
                e[j+1][0] = (d[j+1]+i)%24;
                e[j+1][1] = (-d[j+1]+i+24)%24;
            }
            int left = 1;
            int right = 24;
            while(left+1<right){
                int res = (left+right)/2;
                if(f(e,res,0,seen)) left = res;
                else{
                    right = res;
                }
            }
            if(left>min){
                // if(left==5){
                //     out.println(i);
                // }
                if(left == 1){
                    if(f(e,left,0,seen)){
                        min = left;
                    }
                }
                else min = left;
            }
        }

        ans = min;
        out.println(ans);
    }

    boolean f(int[][] d, int sub, int k, boolean[] seen){
        if(k==n){
            return true;
        }
        boolean res = false;
        for(int i = 0; i < 2; i++){
            boolean[] seenc  = new boolean[25];
            for(int j = 0; j <= 24; j++) seenc[j] = seen[j];
            seenc[d[k][i]] = true;
            if(d[k][i]==0) seenc[24] = true;
            if(d[k][i]==24) seenc[0] = true;
            for(int j = 0; j <= 24; j++){
                if(d[k][i]-sub < j && j < d[k][i]+sub) seenc[j] = true;
                if(d[k][i]-sub < 0 && j > d[k][i]-sub+24) seenc[j] = true;
                if(d[k][i]+sub > 24 && j < d[k][i]+sub-24) seenc[j] = true;
            }
            // for(int g = 0; g <= 24; g++){
            //     if(seenc[g]) out.print(g);
            // }
            // out.println();
            // out.println(d[k][i]);
            // if(!seen[d[k][i]])out.println("ok");
            // else out.println("ng");
            if(!seen[d[k][i]]){
                if(d[k][i]==0){
                    if(!seen[24])res |= f(d, sub, k+1, seenc);
                } 
                else if(d[k][i]==24){
                    if(!seen[0])res |= f(d, sub, k+1, seenc);
                } 
                else res |= f(d, sub, k+1, seenc);
            }
        }  
        return res;
    }   

    void run() throws Exception
    {
        is = INPUT.isEmpty() ? System.in : new ByteArrayInputStream(INPUT.getBytes());
        out = new PrintWriter(System.out);

        long s = System.currentTimeMillis();
        solve();
        out.flush();
        if(!INPUT.isEmpty())tr(System.currentTimeMillis()-s+"ms");
    }

    public static void main(String[] args) throws Exception { new Main().run(); }

    private byte[] inbuf = new byte[1024];
    private int lenbuf = 0, ptrbuf = 0;

    private int readByte()
    {
        if(lenbuf == -1)throw new InputMismatchException();
        if(ptrbuf >= lenbuf){
            ptrbuf = 0;
            try { lenbuf = is.read(inbuf); } catch (IOException e) { throw new InputMismatchException(); }
            if(lenbuf <= 0)return -1;
        }
        return inbuf[ptrbuf++];
    }

    private boolean isSpaceChar(int c) { return !(c >= 33 && c <= 126); }
    private int skip() { int b; while((b = readByte()) != -1 && isSpaceChar(b)); return b; }

    private double nd() { return Double.parseDouble(ns()); }
    private char nc() { return (char)skip(); }

    private String ns()
    {
        int b = skip();
        StringBuilder sb = new StringBuilder();
        while(!(isSpaceChar(b) && b != ' ')){
            sb.appendCodePoint(b);
            b = readByte();
        }
        return sb.toString();
    }

    private char[] ns(int n)
    {
        char[] buf = new char[n];
        int b = skip(), p = 0;
        while(p < n && !(isSpaceChar(b))){
            buf[p++] = (char)b;
            b = readByte();
        }
        return n == p ? buf : Arrays.copyOf(buf, p);
    }

    private char[][] nm(int n, int m)
    {
        char[][] map = new char[n][];
        for(int i = 0;i < n;i++)map[i] = ns(m);
        return map;
    }

    private int[] na(int n)
    {
        int[] a = new int[n];
        for(int i = 0;i < n;i++)a[i] = ni();
        return a;
    }

    private int ni()
    {
        int num = 0, b;
        boolean minus = false;
        while((b = readByte()) != -1 && !((b >= '0' && b <= '9') || b == '-'));
        if(b == '-'){
            minus = true;
            b = readByte();
        }

        while(true){
            if(b >= '0' && b <= '9'){
                num = num * 10 + (b - '0');
            }else{
                return minus ? -num : num;
            }
            b = readByte();
        }
    }

    private long nl()
    {
        long num = 0;
        int b;
        boolean minus = false;
        while((b = readByte()) != -1 && !((b >= '0' && b <= '9') || b == '-'));
        if(b == '-'){
            minus = true;
            b = readByte();
        }

        while(true){
            if(b >= '0' && b <= '9'){
                num = num * 10 + (b - '0');
            }else{
                return minus ? -num : num;
            }
            b = readByte();
        }
    }

    private static void tr(Object... o) { System.out.println(Arrays.deepToString(o)); }

    static class ArrayUtils {
        public static void fill(double[][] array, double value) {
            for (double[] a : array) Arrays.fill(a, value);
        }

        public static void fill(double[][][] array, double value) {
            for (double[][] l : array) fill(l, value);
        } 
    }
}

