import java.io.IOException;
import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.io.InputStream;

/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 */
public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        InputReader in = new InputReader(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        TimeGap solver = new TimeGap();
        solver.solve(1, in, out);
        out.close();
    }

    static class TimeGap {
        int[] D;

        public void solve(int testNumber, InputReader in, PrintWriter out) {
            int N = in.nextInt();
            D = new int[N];
            for (int i = 0; i < N; i++) D[i] = in.nextInt();

            if (N == 1) {
                out.println(Math.min(D[0], 24 - D[0]));
                return;
            }

            if (N > 13) {
                out.println(0);
                return;
            }

            int ans = find(0, N, new int[N]);
            out.println(ans == Integer.MAX_VALUE ? D[0] : ans);
        }

        private int find(int idx, int n, int[] times) {
            if (idx == n) {
                return computeMin(times);
            }
            times[idx] = Math.min(D[idx], 24 - D[idx]);
            int ans = find(idx + 1, n, times);
            times[idx] = Math.max(D[idx], 24 - D[idx]);
            ans = Math.max(ans, find(idx + 1, n, times));
            return ans;
        }

        private int computeMin(int[] times) {
            int ans = Integer.MAX_VALUE;
            for (int i = 0; i < times.length; i++) {
                for (int j = 0; j < times.length; j++) {
                    if (i != j) {
                        ans = Math.min(ans, timeGap(times[i], times[j]));
                    }
                }
            }
            return ans;
        }

        private int timeGap(int t1, int t2) {
            int d = ((t2 - t1) + 48) % 24;
            return Math.min(d, 24 - d);
        }

    }

    static class InputReader {
        public BufferedReader reader;
        public StringTokenizer tokenizer;

        public InputReader(InputStream stream) {
            reader = new BufferedReader(new InputStreamReader(stream), 32768);
            tokenizer = null;
        }

        public String next() {
            while (tokenizer == null || !tokenizer.hasMoreTokens()) {
                try {
                    tokenizer = new StringTokenizer(reader.readLine());
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
            }
            return tokenizer.nextToken();
        }

        public int nextInt() {
            return Integer.parseInt(next());
        }

    }
}

import java.io.PrintWriter;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.Scanner;

public class Main implements Runnable {
	public static void main(String[] args) {
		new Main().run();
	}

	long MOD = (long) (1e9 + 7);

	public void run() {
		Scanner sc = new Scanner(System.in);
		int N = sc.nextInt();
		int[] D = new int[N];
		for (int i = 0; i < N; ++i) {
			D[i] = sc.nextInt();
		}
		boolean[][] ok = new boolean[2][1 << 24];
		ok[0][1] = true;
		for (int i = 0; i < N; ++i) {
			int idx = i % 2;
			Arrays.fill(ok[idx ^ 1], false);
			for (int j = 0; j < 1 << 24; ++j) {
				if (!ok[idx][j])
					continue;
				if ((j & (1 << D[i])) == 0)
					ok[idx ^ 1][j | 1 << D[i]] = true;
				if ((j & (1 << ((24 - D[i]) % 24))) == 0)
					ok[idx ^ 1][j | 1 << ((24 - D[i]) % 24)] = true;
			}
		}
		int cur = N % 2;
		int ans = 0;
		for (int i = 0; i < 1 << 24; ++i) {
			if (ok[cur][i]) {
				int tmp = Integer.MAX_VALUE / 3;
				for (int j = 0; j < 24; ++j) {
					if ((i >> j) % 2 == 0)
						continue;
					int k = j + 1;
					while (k < 24 && (i >> k) % 2 == 0)
						++k;
					if (k == 24) {
						k = 0;
						while ((i >> k) % 2 == 0)
							++k;
						tmp = Math.min(tmp, Math.min(j - k, 24 - (j - k)));
					} else {
						tmp = Math.min(tmp, Math.min(24 - (k - j), k - j));
						j = k - 1;
					}
				}
				ans = Math.max(ans, tmp);
			}
		}
		System.out.println(ans);
	}

	static void tr(Object... objects) {
		System.out.println(Arrays.deepToString(objects));
	}
}

