public class Main {

	private static Scanner sc;
	private static Printer pr;

	private static void solve() {
		int n = sc.nextInt();

		int[] d = new int[n];
		for (int i = 0; i < n; i++) {
			d[i] = sc.nextInt();
		}

		if (n + 1 > 24) {
			pr.println(0);
			return;
		}

		int[] t1 = new int[n + 1];
		int[] t2 = new int[n + 1];
		t1[0] = 0;
		t2[0] = 24;
		for (int i = 0; i < n; i++) {
			t1[i + 1] = d[i];
			t2[i + 1] = 24 - d[i];
		}

		int ans = 0;
		out:
		for (int i = 0; i < 0x1 << n + 1; i++) {
			boolean[] used = new boolean[25];
			int[] t = new int[n + 1];
			for (int j = 0; j <= n; j++) {
				if ((i & 0x1 << j) == 0) {
					t[j] = t1[j];
				} else {
					t[j] = t2[j];
				}
				if (used[t[j]]) {
					continue out;
				} else {
					used[t[j]] = true;
				}
			}

			int min = Integer.MAX_VALUE;
			for (int ii = 0; ii <= n; ii++) {
				for (int jj = ii + 1; jj <= n; jj++) {
					min = Math.min(min, d(t[ii], t[jj]));
				}
			}

			ans = Math.max(ans, min);
		}

		pr.println(ans);
	}

	private static int d(int t1, int t2) {
		if (t1 > t2) {
			int tmp = t2;
			t2 = t1;
			t1 = tmp;
		}

		return Math.min(t2 - t1, 24 - (t2 - t1));
	}

