using System;
using System.Collections;
using System.Collections.Generic;
using System.Diagnostics;
using System.IO;
using System.Linq;
using System.Numerics;
using System.Text;
using static System.Math;
using static Extensions;
using static MathExtensions;

public static class Program
{
    public static void Solve()
    {
        var n = I;
        var d = Repeat(n, () => I).ToArray();

        if (n == 1) { Console.WriteLine(0); return; }

        var list = new List<Pair<int, int>>();

        for (var i = 0; i < n - 1; i++)
            for (var j = i + 1; j < n; j++)
            {
                var t1 = Abs(d[i] - d[j]);
                var t2 = Abs(d[i] - (24 - d[j]));
                if (t1 > t2) Swap(ref t1, ref t2);
                list.Add(Pair.Create(t1, t2));
            }

        var c1 = Comb(n, 2);
        Assert(list.Count == c1);

        var ans = 0;

        Func<int, int> TryInner = c2 =>
        {
            Assert(c1 - c2 > 0);
            var t1 = list.Take(c1 - c2).Min(x => x.First);
            var t2 = c2 > 0 ? list.AsEnumerable().Reverse().Take(c2).Min(x => x.Second) : int.MaxValue;
            return Min(t1, t2);
        };

        Action Try = () =>
        {
            var m1 = n / 2;
            var m2 = n / 2 + (n % 2 == 0 ? 0 : 1);

            while (m1 > 0 && m2 > 0)
            {
                Assert(m1 <= c1 && m2 <= c1);
                var c2 = Comb(m1, 2) + Comb(m2, 2);
                var t = TryInner(c2);
                ans = Max(ans, t);
                m1--; m2++;
            }
        };

        list.Sort((x, y) => y.CompareTo(x));
        Try();
        list = list.Select(p => Pair.Create(p.Second, p.First)).ToList();
