                {
                    if (cnt[j] == 2)
                    {
                        b[11 + j] = true;
                        b[11 - j] = true;
                    }
                    else if (cnt[j] == 1)
                    {
                        if (cp % 2 == 0)
                        {
                            b[11 + j] = true;
                        }
                        else
                        {
                            b[11 - j] = true;
                        }

                        cp /= 2;
