                int before;
                for (before = 23; before >= 0; before--)
                {
                    if (b[before])
                    {

                        break;
                    }
                }

                before -= 24;
                for (int j = 0; j < 24; j++)
                {
                    if (b[j])
                    {
                        min = Math.Min(min, j - before);
                        before = j;

                    }
                }

                ans = Math.Max(ans, min);
            }
            Console.WriteLine(ans);
        }

